      SUBROUTINE MACON
C***BEGIN PROLOGUE  MACON
C***REFER TO  BVSUP
C
C    Sets up machine constants using R1MACH
C***ROUTINES CALLED  R1MACH
C***COMMON BLOCKS    ML5MCO
C***END PROLOGUE  MACON
      COMMON /ML5MCO/URO,SRU,EPS,LPAR,SQOVFL,TWOU,FOURU
C***FIRST EXECUTABLE STATEMENT  MACON
      URO=R1MACH(4)
      SRU=SQRT(URO)
      DD=-ALOG10(URO)
      LPAR=0.5*DD
      KE=0.5+0.75*DD
      EPS=10.**(-2*KE)
      SQOVFL=SQRT(R1MACH(2))
      TWOU=2.0*URO
      FOURU=4.0*URO
      RETURN
      END
