      SUBROUTINE DNL2CP(IV, LIV, LV, P, V)
C
C  ***  PRINT COVARIANCE MATRIX FOR DNL2IT  ***
C
      INTEGER LIV, LV, P
      INTEGER IV(LIV)
      DOUBLE PRECISION V(LV)
C
C  ***  INTRINSIC FUNCTIONS  ***
C/+
      INTEGER IABS, MOD
C/
C
C  ***  LOCAL VARIABLES  ***
C
      INTEGER COV1, I, II, I1, J, PU
C
C     ***  IV SUBSCRIPTS  ***
C
      INTEGER COVMAT, COVPRT, COVREQ, NEEDHD, OUTLEV, PRUNIT
C
C/6
      DATA COVMAT/26/, COVPRT/14/, COVREQ/15/, NEEDHD/36/, OUTLEV/19/,
     1     PRUNIT/21/
C/7
C     PARAMETER (COVMAT=26, COVPRT=14, COVREQ=15, NEEDHD=36, OUTLEV=19,
C    1           PRUNIT=21)
C/
      IF (MOD(IV(COVPRT),2) .EQ. 0) GO TO 999
      IF (IV(1) .GT. 8) GO TO 999
      PU = IV(PRUNIT)
      IF (PU .EQ. 0) GO TO 999
      COV1 = IV(COVMAT)
      IV(NEEDHD) = 1
      IF (COV1) 10,40,60
 10   IF (-1 .EQ. COV1) WRITE(PU,20)
 20   FORMAT(/43H ++++++ INDEFINITE COVARIANCE MATRIX ++++++)
      IF (-2 .EQ. COV1) WRITE(PU,30)
 30   FORMAT(/52H ++++++ OVERSIZE STEPS IN COMPUTING COVARIANCE +++++)
      GO TO 999
C
 40   WRITE(PU,50)
 50   FORMAT(/45H ++++++ COVARIANCE MATRIX NOT COMPUTED ++++++)
      GO TO 999
C
 60   I = IABS(IV(COVREQ))
      IF (I .LE. 1) WRITE(PU,70)
 70   FORMAT(/48H COVARIANCE = SCALE * H**-1 * (J**T * J) * H**-1/
     1       23H WHERE H = F.D. HESSIAN/)
      IF (I .EQ. 2) WRITE(PU,80)
 80   FORMAT(/57H COVARIANCE = H**-1, WHERE H = FINITE-DIFFERENCE HESSIA
     1N/)
      IF (I .GT. 2) WRITE(PU,90)
 90   FORMAT(/30H COVARIANCE = SCALE * J**T * J/)
      II = COV1 - 1
      IF (IV(OUTLEV) .LE. 0) GO TO 120
      DO 100 I = 1, P
         I1 = II + 1
         II = II + I
         WRITE(PU,110) I, (V(J), J = I1, II)
 100     CONTINUE
 110  FORMAT(4H ROW,I3,2X,9D12.4/(9X,9D12.4))
      GO TO 999
C
 120  DO 130 I = 1, P
         I1 = II + 1
         II = II + I
         WRITE(PU,140) I, (V(J), J = I1, II)
 130     CONTINUE
 140  FORMAT(4H ROW,I3,2X,5D12.4/(9X,5D12.4))
C
 999  RETURN
C  ***  LAST CARD OF MLPCVP FOLLOWS  ***
      END
