      DATA NAIF, NAIG, NAIP1, NAIP2 / 4*0 /
      DATA X3SML, X32SML, XBIG / 3*0.D0 /
C***FIRST EXECUTABLE STATEMENT  DAIE
      IF (NAIF.NE.0) GO TO 10
      ETA = 0.1*SNGL(D1MACH(3))
      NAIF = INITDS (AIFCS, 13, ETA)
      NAIG = INITDS (AIGCS, 13, ETA)
      NAIP1 = INITDS (AIP1CS, 57, ETA)
      NAIP2 = INITDS (AIP2CS, 37, ETA)
C
      X3SML = ETA**0.3333E0
      X32SML = 1.3104D0*X3SML**2
      XBIG = D1MACH(2)**0.6666D0
C
 10   IF (X.GE.(-1.0D0)) GO TO 20
      CALL D9AIMP (X, XM, THETA)
      DAIE = XM * DCOS(THETA)
      RETURN
C
 20   IF (X.GT.1.0D0) GO TO 30
      Z = 0.0D0
      IF (DABS(X).GT.X3SML) Z = X**3
      DAIE = 0.375D0 + (DCSEVL (Z, AIFCS, NAIF) - X*(0.25D0 +
     1  DCSEVL (Z, AIGCS, NAIG)) )
      IF (X.GT.X32SML) DAIE = DAIE * DEXP (2.0D0*X*DSQRT(X)/3.0D0)
      RETURN
C
 30   IF (X.GT.4.0D0) GO TO 40
      SQRTX = DSQRT(X)
      Z = (16.D0/(X*SQRTX) - 9.D0)/7.D0
      DAIE = (0.28125D0 + DCSEVL (Z, AIP1CS, NAIP1))/DSQRT(SQRTX)
      RETURN
C
 40   SQRTX = DSQRT(X)
      Z = -1.0D0
      IF (X.LT.XBIG) Z = 16.0D0/(X*SQRTX) - 1.0D0
      DAIE = (0.28125D0 + DCSEVL (Z, AIP2CS, NAIP2))/DSQRT(SQRTX)
      RETURN
C
