      SUBROUTINE CLOSM(IPAGE)
C***BEGIN PROLOGUE  CLOSM
C***REFER TO  SPLP
C     1. UNLOAD, RELEASE, OR CLOSE UNIT NUMBER IPAGEF.
C
C     THIS IS FORTRAN 77 STANDARD FOR THE 'CLOSE' STATEMENT.
C***ROUTINES CALLED  XERRWV
C***END PROLOGUE  CLOSM
C***FIRST EXECUTABLE STATEMENT  CLOSM
      IPAGEF=IPAGE
      CLOSE(UNIT=IPAGEF,IOSTAT=IOS,ERR=100,STATUS='KEEP')
      RETURN
 100  CONTINUE
      IOPT=1
      NERR=100
      CALL XERRWV('SPLP( ). CLOSE() HAS ERROR FLAG (I1).',37,NERR,IOPT,
     *1,IOS,IDUM,0,RDUM,RDUM)
      RETURN
      END
